$.fn.serializeObject=function(){var e={};$("#"+this.selector).find('input[type="hidden"], input[type="text"], input[type="password"], input[type="checkbox"], input[type="radio"]:checked, select').each(function(){if($(this).attr("type")=="hidden"){var t=$(this).parent();var n=t.find('input[type="checkbox"][name="'+this.name.replace(/\[/g,"[").replace(/\]/g,"]")+'"]');if(n!=null){if(n.prop("checked"))return}}if($(this).attr("type")=="checkbox"){var n=$(this)[0];e[n.name]=n.checked;return}if(this.name===null||this.name===undefined||this.name==="")return;var r=null;if($(this).is("select"))r=$(this).find("option:selected").val();else r=this.value;if(e[this.name]!==undefined){if(!e[this.name].push){e[this.name]=[e[this.name]]}e[this.name].push(r||"")}else{e[this.name]=r||""}});return e}